import React, { Component } from 'react';

class JobForm extends Component {
	constructor(props) {
		super(props);

		this.onChangeTodoDescription = this.onChangeTodoDescription.bind(this);
		this.onChangeTodoResponsible = this.onChangeTodoResponsible.bind(this);
		this.onChangeTodoPriority = this.onChangeTodoPriority.bind(this);
		this.onSubmit = this.onSubmit.bind(this);

		this.state = {
			todo_description: '',
			todo_responsible: '',
			todo_priority: '',
			todo_completed: false
		};
	}

	onChangeTodoDescription(e) {
		this.setState({
			todo_description: e.target.value
		});
	}

	onChangeTodoResponsible(e) {
		this.setState({
			todo_responsible: e.target.value
		});
	}

	onChangeTodoPriority(e) {
		this.setState({
			todo_priority: e.target.value
		});
	}

	onSubmit(e) {
		e.preventDefault();

		console.log(`Form submitted:`);
		console.log(`Todo Description: ${this.state.todo_description}`);
		console.log(`Todo Responsible: ${this.state.todo_responsible}`);
		console.log(`Todo Priority: ${this.state.todo_priority}`);

		this.setState({
			todo_description: '',
			todo_responsible: '',
			todo_priority: '',
			todo_completed: false
		});
	}

	render() {
		return (
			<React.Fragment>
				<div class='field'>
					<label class='label'>Company Name</label>
					<div class='control'>
						<input class='input' type='text' placeholder='e.g Microsoft' />
					</div>
				</div>

				<div class='field'>
					<label class='label'>Position</label>
					<div class='control'>
						<input
							class='input'
							type='email'
							placeholder='e.g. Software Engineer'
						/>
					</div>
				</div>

				<div class='field'>
					<label class='label'>Location</label>
					<div class='control'>
						<input class='input' type='text' placeholder='e.g Richmond, VA' />
					</div>
				</div>

				<div class='field'>
					<label class='label'>Submission Date</label>
					<div class='control'>
						<input class='input' type='text' placeholder='e.g May 27, 2019' />
					</div>
				</div>

				<div class='field'>
					<label class='label'>Notes</label>
					<div class='control'>
						<input class='input' type='email' placeholder='e.g. Extra Notes' />
					</div>
				</div>

				<a class='button is-link'>Submit</a>
			</React.Fragment>
		);
	}
}

export default JobForm;
