import React, { Component } from 'react';
import JobForm from '../components/JobForm';

import { Link } from 'react-router-dom';

import axios from 'axios';

class JobFeed extends Component {
	constructor(props) {
		super(props);
		this.state = {
			jobs: [],
			displayJobForm: false
		};
	}

	displayJobForm = () => {
		this.setState({
			displayJobForm: !this.state.displayJobForm
		});
	};

	// gets the data
	componentDidMount() {
		axios
			.get('http://localhost:4000/jobs/')
			.then(response => {
				this.setState({ jobs: response.data });
			})
			.catch(function(error) {
				console.log(error);
			});
	}

	jobList() {
		return this.state.jobs.map(function(currentJob, i) {
			return <Todo todo={currentJob} key={i} />;
		});
	}

	render() {
		let jobForm;

		if (this.state.displayJobForm) {
			jobForm = <JobForm />;
		}
		return (
			<React.Fragment>
				<table className='table'>
					<thead>
						<tr>
							<th>
								<abbr title='Position'>Company Name</abbr>
							</th>
							<th>Position</th>
							<th>Location</th>
							<th>Notes</th>
						</tr>
					</thead>
					<tbody>
						<tr>
							<th>Accenture</th>
							<td>Software Engineer</td>
							<td>NYC, New York</td>
							<td>Company culture is nice and good salary</td>
						</tr>
					</tbody>
				</table>
				<button className='button is-fullwidth' onClick={this.displayJobForm}>
					Add a Job
				</button>
				{jobForm}
			</React.Fragment>
		);
	}
}

export default JobFeed;
