import React, { Component } from 'react';
import axios from 'axios';

class JobForm extends Component {
	constructor(props) {
		super(props);

		this.onChangeJobsCompanyName = this.onChangeJobsCompanyName.bind(this);
		this.onChangeJobsPosition = this.onChangeJobsPosition.bind(this);
		this.onChangeJobsLocation = this.onChangeJobsLocation.bind(this);
		this.onChangeJobsSubmitDate = this.onChangeJobsSubmitDate.bind(this);
		this.onChangeJobsNote = this.onChangeJobsNote.bind(this);
		this.onSubmit = this.onSubmit.bind(this);

		this.state = {
			jobs_company_name: '',
			jobs_position: '',
			jobs_location: '',
			jobs_submit_date: '',
			jobs_note: ''
		};
	}

	onChangeJobsCompanyName(e) {
		this.setState({
			jobs_company_name: e.target.value
		});
	}

	onChangeJobsPosition(e) {
		this.setState({
			jobs_position: e.target.value
		});
	}

	onChangeJobsLocation(e) {
		this.setState({
			jobs_location: e.target.value
		});
	}

	onChangeJobsSubmitDate(e) {
		this.setState({
			jobs_submit_date: e.target.value
		});
	}

	onChangeJobsNote(e) {
		this.setState({
			jobs_note: e.target.value
		});
	}

	onSubmit(e) {
		e.preventDefault();

		console.log(`Form submitted:`);
		console.log(`Todo Description: ${this.state.todo_description}`);
		console.log(`Todo Responsible: ${this.state.todo_responsible}`);
		console.log(`Todo Priority: ${this.state.todo_priority}`);

		this.setState({
			todo_description: '',
			todo_responsible: '',
			todo_priority: '',
			todo_completed: false
		});
	}

	render() {
		return (
			<React.Fragment>
				<div class='field'>
					<label class='label'>Company Name</label>
					<div class='control'>
						<input class='input' type='text' placeholder='e.g Microsoft' />
					</div>
				</div>

				<div class='field'>
					<label class='label'>Position</label>
					<div class='control'>
						<input
							class='input'
							type='email'
							placeholder='e.g. Software Engineer'
						/>
					</div>
				</div>

				<div class='field'>
					<label class='label'>Location</label>
					<div class='control'>
						<input class='input' type='text' placeholder='e.g Richmond, VA' />
					</div>
				</div>

				<div class='field'>
					<label class='label'>Submission Date</label>
					<div class='control'>
						<input class='input' type='text' placeholder='e.g May 27, 2019' />
					</div>
				</div>

				<div class='field'>
					<label class='label'>Notes</label>
					<div class='control'>
						<input class='input' type='email' placeholder='e.g. Extra Notes' />
					</div>
				</div>

				<a class='button is-link'>Submit</a>
			</React.Fragment>
		);
	}
}

export default JobForm;
